Module.Content:
		
		#requireResource("${atlassian.plugin.key}:gadget-common")
        #includeResources()
		<script type="text/javascript">
		gadgets.window.setTitle("__MSG_sonar.gadget.treemap.title__");
		var WAIT_IMAGE_SRC = "__ATLASSIAN_BASE_URL__" + "/download/resources/${atlassian.plugin.key}/images/wait.gif";
		(function () {
			AJS.Gadget({
				baseUrl: "__ATLASSIAN_BASE_URL__",
				useOauth: "/rest/gadget/1.0/currentUser",
				config: {
					descriptor: function (args) {
						var gadget = this;
						gadgets.window.setTitle("__MSG_sonar.gadget.treemap.title__");
						return  {
							action : "",
							theme : AJS.gadget.sonar.config.getThemeConfig(gadgets),
							fields: AJS.gadget.sonar.fields.generateServerAndProjectPickerFields(gadget, "sonarServer", "sonarProject", {label: "__MSG_sonar.gadget.all.projects__", value: ""})
						};
					},
					args: []
				},
				view: {
					enableReload: true,
					onResizeReload: true,
					template: function (args) {
						var gadget = this;
						gadget.getView().empty();
						gadget.showLoading();
						if (args.measureData.length == 1) {
							gadgets.window.setTitle(AJS.format("__MSG_sonar.gadget.violations.project.title__", args.measureData[0].name));
						} else {
							gadgets.window.setTitle(AJS.format("__MSG_sonar.gadget.violations.project.title__", "__MSG_sonar.gadget.all.projects__"));
						}
						var dimensions = {
							width: gadget.getView().width() - 20,
							height: gadget.getView().width() - 20,
						};
						var onChangeCallback = function(sizeMetric, colorMetric) {
							AJS.$.ajax({
								type: "GET",
								dataType: "json",
								cache: false,
								global: false,
								url: gadget.getBaseUrl() + "/rest/gadget/1.0/currentUser",
								success: function() {
									gadget.savePref("sizeMetric", sizeMetric.key);
									gadget.savePref("colorMetric", colorMetric.key);
								},
								error: function(request, textStatus) {
								}
							});
						};
						gadget.getView().append(
							AJS.sonar.views.generateTreemapView(gadget.getBaseUrl(), gadget.getPref("sonarServer"), gadget.getPref("sonarProject"),
																args.metricsDetails, gadget.getPref("sizeMetric"), gadget.getPref("colorMetric"),
																dimensions, onChangeCallback)
						);
						gadget.getView().height((dimensions.height + 50) + "px");
						gadget.hideLoading();
						gadget.resize();
					},
					args: [{
						key: "measureData",
						ajaxOptions: function () {
							var gadget = this;
							return {
								url: AJS.sonar.accessor.generateApiUrl(gadget.getPref("sonarServer"), gadget.getPref("sonarProject"), "")
							};
						}
					}, {
						key: "metricsDetails",
						ajaxOptions: function () {
							var gadget = this;
							return {
								url: AJS.sonar.accessor.generateServerMetricsApiUrl(gadget.getPref("sonarServer"))
							};
						}
					}]
				}
			});
		})();
		</script>
